// Arithmetic
let a = 1 + 2;
let b = 3 - 4;
let c = 5 * 6;
let d = 7 / 8;
let e = 9 % 10;

// Comparison
let eq = a == b;
let neq = a != b;
let lt = a < b;
let gt = a > b;

// Logical
let and = true && false;
let or = true || false;
let not = !true;

// Bitwise
let band = a & b;
let bor = a | b;
let xor = a ^ b;

// Reference
let r = &x;
let m = &mut y;
